package assignment1;

public class SwapNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		int b = 20;
		System.out.println ("a and b before swap "+ a + " and "+b);
		int c = a;
		a=b;
		b=c;
		System.out.println ("a and b before swap "+ a + " and "+b);
				

	}

}
